import { useState, memo, useRef } from 'react'
import { Link } from 'react-router-dom'
import Hotel1 from '../../images/hotel-detail (1).png'
import Hotel2 from '../../images/hotel-detail (2).png'
import Hotel3 from '../../images/hotel-detail (3).png'
import Restaurant1 from '../../images/buffet.png'
import Restaurant2 from '../../images/casual.png'
import Restaurant3 from '../../images/snack.png'
import Restaurant4 from '../../images/otherRestaurant.png'
import './SelectDetailService.scss'


const campingServiceText = ['accommodation', 'attractions', 'restaurants']

const informationDetailService = [[{
    logo: Hotel3,
    title: 'Hotel',
    description: 'A commercial accommodation that provides private rooms with meals and guest services.'
},
{
    logo: Hotel1,
    title: 'Home Stay',
    description: 'A budget accommodation where the guest has private room while sharing some shared areas with other guests.'
},
{
    logo: Hotel2,
    title: 'Motel',
    description: "Guests will have the whole place to themselves—they don't have to share facilities such as bathrooms, common rooms, and kitchen with other guests."
}], [{
    logo: Hotel1,
    title: 'Buffet restaurant',
    description: 'A commercial accommodation that provides private rooms with meals and guest services.'
},
{
    logo: Hotel2,
    title: 'Casual Dining restaurant',
    description: 'A budget accommodation where the guest has private room while sharing some shared areas with other guests.'
},
{
    logo: Hotel3,
    title: 'Snack Bar Restaurant',
    description: "Guests will have the whole place to themselves—they don't have to share facilities such as bathrooms, common rooms, and kitchen with other guests."
},
{
    logo: Hotel3,
    title: 'Other',
    description: "Guests will have the whole place to themselves—they don't have to share facilities such as bathrooms, common rooms, and kitchen with other guests."
}], [{
    logo: Restaurant1,
    title: 'Buffet restaurant',
    description: 'Customers can walk, stand or sit as they like when dining according to the self-service model according to their preferences.'
},
{
    logo: Restaurant2,
    title: 'Casual Dining restaurant',
    description: 'A budget accommodation where the guest has private room while sharing some shared areas with other guests.'
},
{
    logo: Restaurant3,
    title: 'Snack Bar Restaurant',
    description: "Guests will have the whole place to themselves—they don't have to share facilities such as bathrooms, common rooms, and kitchen with other guests."
},
{
    logo: Restaurant4,
    title: 'Other',
    description: "Guests will have the whole place to themselves—they don't have to share facilities such as bathrooms, common rooms, and kitchen with other guests."
}]]

function SelectDetailService() {
    const categoryService = sessionStorage.getItem('index-service-selected')
    const [selectIndexDetailService, setSelectIndexDetailService] = useState(0)
    const [selectDetailService, setSelectDetailService] = useState(informationDetailService[categoryService][0].title)

    const handleSelectDetailService = (index) => {
        sessionStorage.setItem('index-detail-service-selected', index)
        setSelectIndexDetailService(index)
        setSelectDetailService(informationDetailService[categoryService][index].title)
    }

    return (<div className='detail-service'>
        <div className='container'>
            <label
                className='title title-select-detail-service'>
                {`Let's talk about the category of your ${campingServiceText[categoryService]}!`}
            </label>
            <div>
                {informationDetailService[categoryService].map((item, index) => (
                    <div key={index}
                        className={`d-flex item-detail-service ${selectIndexDetailService === index ? 'selected' : ''}`}
                        onClick={() => handleSelectDetailService(index)}>
                        <img src={item.logo} className='logo-item-detail-service' />
                        <div className='description-detail-service'>
                            <label className='d-block title-detail-service'>{item.title}</label>
                            <label>{item.description}</label>
                        </div>
                    </div>
                ))}
            </div>
            <div className='location-btn-submit'>
                <Link to='/partner/register-information-service' className='btn btn-primary btn-submit-detail-service'>Submit</Link>
            </div>
        </div>
    </div>)
}

export default memo(SelectDetailService)
