import { memo, useState, useEffect } from 'react'
import { english, vietnamese } from '../../Languages/GeneralInformationTour'
import { english as englishCategory, vietnamese as vietnameseCategory } from '../../Languages/TourCategory'
import City from '../../Data/city.json'
import './GeneralInformationTour.scss'

function GeneralInformationTour({ languageSelected, tour, setTour }) {
    const languageList = (languageSelected === 'EN' ? english : vietnamese)
    const categorys = (languageSelected === 'EN' ? englishCategory : vietnameseCategory)

    const handleBlur = (input) => {
        input.style.border = 'solid 1px #D9D9D9'
    }

    const handleFocus = (input) => {
        input.style.border = 'solid 1px #4874E8'
    }

    return (
        <div className='d-flex'>
            <div className='w-50'>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block">{languageList.txtCode}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='text' />
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block">{languageList.txtName}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='text' />
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block">{languageList.txtCategory}</label>
                        <select onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='text'>
                            {categorys.map((item, index) => (
                                index !== 0 && <option>{item.label}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 ">
                        <label htmlFor="companyEmail" className="d-block">{languageList.txtStartPlace}</label>
                        <select onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='text'>
                            {City.map((item) => (
                                <option>{item.name}</option>
                            ))}
                        </select>
                    </div>
                    <div className="mlr-50">
                        <label htmlFor="companyPhone" className="d-block">{languageList.txtEndPlace}</label>
                        <select onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='text'>
                            {City.map((item) => (
                                <option>{item.name}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 ">
                        <label htmlFor="companyEmail" className="d-block">{languageList.txtMinAdult}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='number' min={1} />
                    </div>
                    <div className="mlr-50">
                        <label htmlFor="companyPhone" className="d-block">{languageList.txtMaxAdult}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='number' min={1} />
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 ">
                        <label htmlFor="companyEmail" className="d-block">{languageList.txtMinChildren}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='number' min={1} />
                    </div>
                    <div className="mlr-50">
                        <label htmlFor="companyPhone" className="d-block">{languageList.txtMaxChildren}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='number' min={1} />
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block">{languageList.txtMinToActive}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='number' min={1} />
                    </div>
                </div>
            </div>
            <div className='w-50'>
                asd
            </div>
        </div>
    )
}

export default memo(GeneralInformationTour)
