import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'
import { Fragment, useState } from 'react';
import { publicRoute } from './routes'
import { DefaultLayout } from './components/Layout';
import { ToastContainer } from 'react-toastify'
import LoadingBar from 'react-top-loading-bar'
import 'bootstrap/dist/css/bootstrap.min.css'
import 'bootstrap/dist/js/bootstrap.bundle'

function App() {
  let cokkieArr = document.cookie;
  let language = (cokkieArr.split("languageSelected=")[1] ? 'EN' : cokkieArr.split("languageSelected=")[1].split(';')[0])
  let email = cokkieArr.split("email=")[1]
  let password = cokkieArr.split("password=")[1]
  const [languageSelected, setLanguageSelected] = useState(language)

  return (
    <Router>
      <div className="App">

        <Routes>
          {publicRoute.map((route, index) => {
            let Layout = DefaultLayout;
            let image;
            let role;
            let changePassword = false;

            if (route.layout) { Layout = route.layout }
            else if (route.layout === null) { Layout = Fragment }

            if (route.image !== null) { image = route.image }

            if (route.role !== null) { role = route.role }

            if (route.changePassword) { changePassword = true }

            const Page = route.component
            return <Route key={index}
              path={route.path}
              element={<Layout changePassword={changePassword} languageSelected={languageSelected} setLanguageSelected={setLanguageSelected} image={image}>
                <Page languageSelected={languageSelected} role={role} />
              </Layout>} />
          })}
        </Routes>
        <ToastContainer />
      </div>
    </Router>
  );
}

export default App;
