import { memo, useState, useEffect } from 'react'
import { useLocation } from 'react-router-dom'
import './HistoryBookingCustomer.scss'
import { english, vietnamese } from '../../Languages/HistoryBookingCustomer'

function HistoryBookingCustomer({ languageSelected, listBooking }) {
    const [statusBooking, setStatusBooking] = useState(-1)

    let listBookingShow = []

    useEffect(() => {
        if (statusBooking === 0) {
            listBookingShow = [...listBooking]
        }
        else {
            listBooking.forEach((item) => {
                if (item.status == statusBooking) {
                    listBookingShow.push(item)
                }
            })
        }
    }, [statusBooking])

    const today = new Date().toISOString().split("T")[0]
    const languageList = languageSelected === 'EN' ? english : vietnamese

    const formatter = new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'VND'
    });

    return (
        <div className='container'>
            <nav className='d-flex br-all'>
                <div onClick={() => setStatusBooking(-1)} className={`w-25 text-center tab-history-booking bold ${statusBooking === -1 && 'tab-history-booking-selected'}`}>{languageList.txtAllBooking}</div>
                <div onClick={() => setStatusBooking(0)} className={`w-25 text-center tab-history-booking bold ${statusBooking === 0 && 'tab-history-booking-selected'}`}>{languageList.txtWaitPay}</div>
                <div onClick={() => setStatusBooking(1)} className={`w-25 text-center tab-history-booking bold ${statusBooking === 1 && 'tab-history-booking-selected'}`}>{languageList.txtPaid}</div>
                <div onClick={() => setStatusBooking(1)} className={`w-25 text-center tab-history-booking bold ${statusBooking === 2 && 'tab-history-booking-selected'}`}>{languageList.txtCanceled}</div>
            </nav>
            <div className='mt-20'>
                {listBooking.map((item, index) => (
                    <div key={index} className='mb-20 item-list-history-booking d-flex'>
                        <div>
                            <div className='tour-name-history-booking mt-10'>{item.tourName}</div>
                            <div className='start-date-history-booking mt-10'>{item.startDate}</div>
                            <div className='start-date-history-booking mt-10'>{`${languageList.txtAdult} ${item.numberOfAdult}, ${languageList.txtChildren} ${item.numberOfChildren}`}</div>
                            <div className='mt-10'>
                                {(item.tourType == 0 && item.status == 0 && today < item.startDate && <button className='btn-pay-history-booking'>{languageList.txtPayNow}</button>) ||
                                    (item.tourType == 0 && item.status == 1 && today < item.startDate && <button className='btn-cancel-history-booking'>{languageList.txtCancel}</button>) ||
                                    (item.tourType == 0 && item.status == 1 && today >= item.startDate && <div className='price-payed-history-booking'>{languageList.txtPaid} {formatter.format(item.price)}<button className='btn-feedback-history-booking'>{languageList.txtFeedback}</button></div>) ||
                                    (item.tourType == 0 && item.status == 2 && today >= item.startDate && <label className='canceled-history-booking'>{languageList.txtCanceled}</label>) ||
                                    (item.tourType == 1 && item.status == 1 && today < item.startDate && <button className='btn-cancel-history-booking'>{languageList.txtCancel}</button>) ||
                                    (item.tourType == 1 && item.status == 1 && today >= item.startDate && <div className='price-payed-history-booking'>{languageList.txtPaid} {formatter.format(item.price)}<button className='btn-feedback-history-booking'>{languageList.txtFeedback}</button></div>) ||
                                    (item.tourType == 1 && item.status == 2 && today >= item.startDate && <label className='canceled-history-booking'>{languageList.txtCanceled}</label>) ||
                                    (item.tourType == 2 && item.status == 0 && !item.statusDeposit && today < item.startDate && <button className='btn-pay-history-booking'>{languageList.txtPayDeposit}</button>) ||
                                    (item.tourType == 2 && item.status == 0 && !item.statusDeposit && today >= item.startDate && <label className='canceled-history-booking'>{languageList.txtCanceled}</label>) ||
                                    (item.tourType == 2 && item.status == 0 && item.statusDeposit && today >= item.startDate && <button className='btn-pay-history-booking'>{languageList.txtPayNow}</button>) ||
                                    (item.tourType == 2 && item.status == 0 && item.statusDeposit && today < item.startDate && <label className='waiting-history-booking'>{languageList.txtWaiting}</label>) ||
                                    (item.tourType == 2 && item.status == 1 && item.statusDeposit && today >= item.startDate && <div className='price-payed-history-booking'>{languageList.txtPaid} {formatter.format(item.price)}<button className='btn-feedback-history-booking'>{languageList.txtFeedback}</button></div>) ||
                                    (item.tourType == 2 && item.status == 2 && <label className='canceled-history-booking'>{languageList.txtCanceled}</label>)}
                            </div>
                        </div>
                        <div>Xem chi tiet item</div>
                    </div>
                ))}

            </div>
        </div>
    )
}

export default memo(HistoryBookingCustomer)
