import { memo } from 'react'
import './MoreInformationTour.scss'
import { english, vietnamese } from '../../Languages/MoreInformationTour'

function MoreInformationTour({ languageSelected, tour, setTour }) {
    const languageList = (languageSelected === 'EN' ? english : vietnamese)

    return (
        <div className='d-flex form-general-tour bg-none'>
            <div className='w-50 mr-10 item-lr'>
                <div>
                    <div className="d-flex line-input">
                        <div className="mlr-50 input-alone">
                            <label className="d-block title-create-tour">{languageList.txtCode}</label>
                            <input onFocus={(e) => handleFocus(e.target)}
                                onBlur={(e) => handleBlur(e.target)}
                                value={tour.code}
                                onChange={(e) => setTour({ ...tour, code: e.target.value })}
                                className="input-inline" type='text' />
                        </div>
                    </div>
                </div>
                <div></div>
            </div>
            <div className='w-50 ml-10 item-lr'>right</div>
        </div>
    )
}

export default memo(MoreInformationTour)
