import { memo, useState, useEffect } from 'react'
import { english, vietnamese } from '../../Languages/GeneralInformationTour'
import { english as englishCategory, vietnamese as vietnameseCategory } from '../../Languages/TourCategory'
import City from '../../Data/city.json'
import './GeneralInformationTour.scss'

function GeneralInformationTour({ languageSelected, tour, setTour }) {
    const languageList = (languageSelected === 'EN' ? english : vietnamese)
    const categorys = (languageSelected === 'EN' ? englishCategory : vietnameseCategory)

    const handleBlur = (input) => {
        input.style.border = 'solid 1px #D9D9D9'
    }

    const handleFocus = (input) => {
        input.style.border = 'solid 1px #4874E8'
    }

    const handleInputNumberOfDay = (value) => {
        let tourRaw = { ...tour }
        tourRaw.numberOfDay = parseInt(value)
        if (value < tour.numberOfNight) {
            tourRaw.numberOfNight = parseInt(value)
        }
        setTour(tourRaw)
    }

    return (
        <div className='d-flex form-general-tour'>
            <div className='w-50'>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block title-create-tour">{languageList.txtCode}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            value={tour.code}
                            onChange={(e) => setTour({ ...tour, code: e.target.value })}
                            className="input-inline" type='text' />
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block title-create-tour">{languageList.txtName}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            value={tour.name}
                            onChange={(e) => setTour({ ...tour, name: e.target.value })}
                            className="input-inline" type='text' />
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block title-create-tour">{languageList.txtCategory}</label>
                        <select onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='text'
                            onChange={(e) => setTour({ ...tour, category: e.target.value })}>
                            {categorys.map((item, index) => (
                                index !== 0 && <option selected={(tour.category == item.value)} value={item.value}>{item.label}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 ">
                        <label className="d-block title-create-tour" title-create-tour>{languageList.txtStartPlace}</label>
                        <select onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='text'
                            onChange={(e) => setTour({ ...tour, startPlace: e.target.value })}>
                            {City.map((item) => (
                                <option value={item.name} selected={(tour.startPlace == item.name)}>{item.name}</option>
                            ))}
                        </select>
                    </div>
                    <div className="mlr-50">
                        <label className="d-block title-create-tour">{languageList.txtEndPlace}</label>
                        <select onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            className="input-inline" type='text'
                            onChange={(e) => setTour({ ...tour, endPlace: e.target.value })}>
                            {City.map((item) => (
                                <option value={item.name} selected={(tour.endPlace == item.name)}>{item.name}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 ">
                        <label className="d-block title-create-tour">{languageList.txtMinAdult}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            value={tour.minAdult}
                            onChange={(e) => setTour({ ...tour, minAdult: e.target.value })}
                            className="input-inline" type='number' min={1} />
                    </div>
                    <div className="mlr-50">
                        <label className="d-block title-create-tour">{languageList.txtMaxAdult}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            value={tour.maxAdult}
                            onChange={(e) => setTour({ ...tour, maxAdult: e.target.value })}
                            className="input-inline" type='number' min={1} />
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 ">
                        <label className="d-block title-create-tour">{languageList.txtMinChildren}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            value={tour.minChildren}
                            onChange={(e) => setTour({ ...tour, minChildren: e.target.value })}
                            className="input-inline" type='number' min={1} />
                    </div>
                    <div className="mlr-50">
                        <label className="d-block title-create-tour">{languageList.txtMaxChildren}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            value={tour.maxChildren}
                            onChange={(e) => setTour({ ...tour, maxChildren: e.target.value })}
                            className="input-inline" type='number' min={1} />
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block title-create-tour">{languageList.txtMinToActive}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            value={tour.minToActive}
                            onChange={(e) => setTour({ ...tour, minToActive: e.target.value })}
                            className="input-inline" type='number' min={1} />
                    </div>
                </div>
            </div>
            <div className='w-50'>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block title-create-tour">{languageList.txtStatus}</label>
                        <div className="d-flex line-input mt-10 w-70">
                            <label htmlFor='status-active' className='d-flex radio-custom input-inline'>
                                <input name='status' type='radio' id='status-active' value='1' hidden
                                    onChange={(e) => setTour({ ...tour, status: parseInt(e.target.value) })} />
                                <div htmlFor='status-active' className={`radio-spuare mr-20 ${tour.status === 1 && 'radio-custom-checked'}`} />
                                <label htmlFor='status-active'>{languageList.txtActive}</label>
                            </label>
                            <label htmlFor='status-waiting' className='d-flex radio-custom input-inline'>
                                <input name='status' type='radio' id='status-waiting' value='2' hidden
                                    onChange={(e) => setTour({ ...tour, status: parseInt(e.target.value) })} />
                                <div htmlFor='status-waiting' className={`radio-spuare mr-20 ${tour.status === 2 && 'radio-custom-checked'}`} />
                                <label htmlFor='status-waiting'>{languageList.txtWaiting}</label>
                            </label>
                        </div>
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block title-create-tour">{languageList.txtType}</label>
                        <div className="d-flex line-input mt-10 w-70">
                            <label htmlFor='type-private' className='d-flex radio-custom input-inline'>
                                <input name='status' type='radio' id='type-private' value='0' hidden
                                    onChange={(e) => setTour({ ...tour, type: parseInt(e.target.value) })} />
                                <div htmlFor='type-private' className={`radio-spuare mr-20 ${tour.type === 0 && 'radio-custom-checked'}`} />
                                <label htmlFor='type-private'>{languageList.txtPrivate}</label>
                            </label>
                            <label htmlFor='type-sic' className='d-flex radio-custom input-inline'>
                                <input name='status' type='radio' id='type-sic' value='1' hidden
                                    onChange={(e) => setTour({ ...tour, type: parseInt(e.target.value) })} />
                                <div htmlFor='type-sic' className={`radio-spuare mr-20 ${tour.type === 1 && 'radio-custom-checked'}`} />
                                <label htmlFor='type-sic'>{languageList.txtSIC}</label>
                            </label>
                        </div>
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="mlr-50 input-alone">
                        <label className="d-block title-create-tour">{languageList.txtMode}</label>
                        <div className="d-flex line-input mt-10 w-70">
                            <label htmlFor='mode-day' className='d-flex radio-custom input-inline'>
                                <input name='status' type='radio' id='mode-day' value='0' hidden
                                    onChange={(e) => setTour({ ...tour, mode: parseInt(e.target.value) })} />
                                <div htmlFor='mode-day' className={`radio-spuare mr-20 ${tour.mode === 0 && 'radio-custom-checked'}`} />
                                <label htmlFor='mode-day'>{languageList.txtDayTour}</label>
                            </label>
                            <label htmlFor='mode-multi-day' className='d-flex radio-custom input-inline'>
                                <input name='status' type='radio' id='mode-multi-day' value='1' hidden
                                    onChange={(e) => setTour({ ...tour, mode: parseInt(e.target.value) })} />
                                <div htmlFor='mode-multi-day' className={`radio-spuare mr-20 ${tour.mode === 1 && 'radio-custom-checked'}`} />
                                <label htmlFor='mode-multi-day'>{languageList.txtMultiDayTour}</label>
                            </label>
                        </div>
                    </div>
                </div>
                {tour.mode === 1 ?
                    <div className="d-flex line-input">
                        <div className="mlr-50 ">
                            <label className="d-block title-create-tour">{languageList.txtNumberOfDay}</label>
                            <input onFocus={(e) => handleFocus(e.target)}
                                onBlur={(e) => handleBlur(e.target)}
                                value={tour.numberOfDay}
                                onChange={(e) => handleInputNumberOfDay(e.target.value)}
                                className="input-inline" type='number' min={1} />
                        </div>
                        <div className="mlr-50">
                            <label className="d-block title-create-tour">{languageList.txtNumberOfNight}</label>
                            <input onFocus={(e) => handleFocus(e.target)}
                                onBlur={(e) => handleBlur(e.target)}
                                value={tour.numberOfNight}
                                onChange={(e) => setTour({ ...tour, numberOfNight: parseInt(e.target.value) })}
                                className="input-inline" type='number' min={1} max={tour.numberOfDay} />
                        </div>
                    </div>
                    :
                    <div className="d-flex line-input">
                        <div className="mlr-50 ">
                            <label className="d-block title-create-tour">{languageList.txtStartTime}</label>
                            <input onFocus={(e) => handleFocus(e.target)}
                                onBlur={(e) => handleBlur(e.target)}
                                value={tour.startTime}
                                onChange={(e) => setTour({ ...tour, startTime: e.target.value })}
                                className="input-inline" type='time' min={1} />
                        </div>
                        <div className="mlr-50">
                            <label className="d-block title-create-tour">{languageList.txtEndTime}</label>
                            <input onFocus={(e) => handleFocus(e.target)}
                                onBlur={(e) => handleBlur(e.target)}
                                value={tour.endTime}
                                onChange={(e) => setTour({ ...tour, endTime: e.target.value })}
                                className="input-inline" type='time' min={1} />
                        </div>
                    </div>
                }
            </div>
        </div>
    )
}

export default memo(GeneralInformationTour)
