import { memo, useState, useEffect } from 'react'
import { Sidebar, Menu, MenuItem, SubMenu, useProSidebar } from 'react-pro-sidebar';
import { englishAdminMenu, vietnameseAdminMenu } from '../../Languages/AdminMenu';
import { AiFillCaretLeft, AiFillCaretRight } from 'react-icons/ai'
import Dashboard from '../../images/dashboard.png'
import Suppliers from '../../images/supplier.png'
import Services from '../../images/services.png'
import Booking from '../../images/booking.png'
import Customers from '../../images/customer.png'
import Partners from '../../images/partners.png'
import Feedback from '../../images/feedback.png'
import ServiceConfirm from '../../images/service_confirm.png'
import './AdminHome.scss'

function AdminHome({ languageSelected, children }) {
    let adminMenuLabel = (languageSelected === 'EN' ? englishAdminMenu : vietnameseAdminMenu)
    const pathName = window.location.pathname

    const { collapseSidebar } = useProSidebar();
    const [isOpenSidebar, setOpenSidebar] = useState(true)

    const handleClickOpenCloseSidebar = () => {
        setOpenSidebar(!isOpenSidebar);
        collapseSidebar();
    }

    return (
        <div className='container home-main'>
            <div className='home-partner-main d-flex'>
                <Sidebar className='menu-sidebar-admin'>
                    <Menu>
                        <MenuItem className='menu-item-left' onClick={handleClickOpenCloseSidebar} icon={isOpenSidebar ? <AiFillCaretLeft /> : <AiFillCaretRight />} />
                        <MenuItem icon={<img className='image-icon-sidebar' src={Dashboard} />}
                            className={`menu-item-left ${pathName === '/admin/dashboard' && 'selected'}`}>
                            {adminMenuLabel.txtDashboard}
                        </MenuItem>
                        <SubMenu icon={<img className='image-icon-sidebar' src={Suppliers} />} className='menu-item-left menu-sub-main' label={adminMenuLabel.txtSuppliers}>
                            <MenuItem className={`menu-item-left ${pathName === '/admin/suppliers/accommodation' && 'selected'}`}>
                                {adminMenuLabel.txtAccommodation}
                            </MenuItem>
                            <MenuItem className='menu-item-left'>{adminMenuLabel.txtEntertainment}</MenuItem>
                            <MenuItem className='menu-item-left'>{adminMenuLabel.txtAccommodation}</MenuItem>
                        </SubMenu>
                        <SubMenu icon={<img className='image-icon-sidebar' src={Services} />} className='menu-item-left menu-sub-main' label={adminMenuLabel.txtService}>
                            <MenuItem className='menu-item-left'>{adminMenuLabel.txtTourProduct}</MenuItem>
                        </SubMenu>
                        <MenuItem icon={<img className='image-icon-sidebar' src={Booking} />} className='menu-item-left'>{adminMenuLabel.txtBooking}</MenuItem>
                        <MenuItem icon={<img className='image-icon-sidebar' src={Customers} />} className='menu-item-left'>{adminMenuLabel.txtCustomer}</MenuItem>
                        <MenuItem icon={<img className='image-icon-sidebar' src={Partners} />} className='menu-item-left'>{adminMenuLabel.txtPartner}</MenuItem>
                        <MenuItem icon={<img className='image-icon-sidebar' src={Feedback} />} className='menu-item-left'>{adminMenuLabel.txtFeedback}</MenuItem>
                        <MenuItem icon={<img className='image-icon-sidebar' src={ServiceConfirm} />} className='menu-item-left'>{adminMenuLabel.txtServiceConfirm}</MenuItem>
                    </Menu>
                </Sidebar>
                <div className='container list-service-main'>
                    {children}
                </div>
            </div>
        </div>
    )
}

export default memo(AdminHome)
