import { memo } from 'react'
import Select from 'react-select';

function RegisterUtilitiesAndServices() {
    const handleBlur = (input) => {
        input.style.border = 'solid 1px #D9D9D9'
    }

    const handleFocus = (input) => {
        input.style.border = 'solid 1px #4874E8'
    }

    const Internet = [
        {
            value: 0,
            label: 'Wifi Free'
        }
        , {
            value: 1,
            label: 'Lan Internet Access'
        },
        {
            value: 2,
            label: 'INTERNATIONAL_CALL'
        }]

    return (
        <div className='container'>
            <div className='space-30 background-w'>
                <div className="d-flex line-input line-input-3-line">
                    <div className="w-45">
                        <label htmlFor='Internet' className='d-block'>Internet</label>
                        <Select id='Internet' className='input-inline basic-multi-select'
                            defaultValue={[Internet[0]]}
                            isMulti
                            classNamePrefix="select"
                            options={Internet}
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)} />
                    </div>
                    <div className='w-45'>
                        <label htmlFor="bedType" className="d-block">Star rating<span className="requird-star">*</span></label>
                        <input id='roomName' className='input-inline'
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default memo(RegisterUtilitiesAndServices)
