import { memo, useState } from 'react'
import { english, vietnamese } from '../../Languages/TourSchedule'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './TourSchedule.scss'
import Select from 'react-select';

function TourSchedule({ languageSelected, tourSchedule, setTourSchedule }) {
    const languageList = (languageSelected === 'EN' ? english : vietnamese)

    const handleBlur = (input) => {
        input.style.border = 'solid 1px #D9D9D9'
    }

    const handleFocus = (input) => {
        input.style.border = 'solid 1px #4874E8'
    }

    const [serviceList, setServiceList] = useState([
        {
            value: 1,
            label: 'Hotel abc'
        },
        {
            value: 2,
            label: 'Entertainment abc'
        },
        {
            value: 3,
            label: 'Restaurant abc'
        },
    ])

    return (
        <>
            <div className="d-flex line-input">
                <div className="mlr-50 input-alone">
                    <label className="d-block title-create-tour">{languageList.txtDay}</label>
                    <input onFocus={(e) => handleFocus(e.target)}
                        onBlur={(e) => handleBlur(e.target)}
                        className="input-inline " type='text' />
                </div>
            </div>
            <div className="d-flex line-input">
                <div className="mlr-50 input-alone">
                    <label className="d-block title-create-tour">{languageList.txtContent}</label>
                    <ReactQuill onFocus={(e) => handleFocus(e.target)}
                        onBlur={(e) => handleBlur(e.target)}
                        className="input-inline border-none quill-create-tour" theme="snow" />
                </div>
            </div>
            <div className="d-flex line-input">
                <div className="mlr-50 input-alone">
                    <label className="d-block title-create-tour">{languageList.txtDestination}</label>
                    <input onFocus={(e) => handleFocus(e.target)}
                        onBlur={(e) => handleBlur(e.target)}
                        className="input-inline" type='text' />
                </div>
            </div>
            <div className="d-flex line-input">
                <div className="mlr-50 input-alone">
                    <label className="d-block title-create-tour">{languageList.txtServices}</label>
                    <Select onFocus={(e) => handleFocus(e.target)}
                        onBlur={(e) => handleBlur(e.target)}
                        className="input-inline border-none" placeholder={languageList.txtServices}
                        options={serviceList} isMulti />
                </div>
            </div>
        </>
    )
}

export default memo(TourSchedule)
